    public static function translateRule34()
    {
        $videos = Video::where('sd', 'ilike', '%rule34.xxx%')->where('tags_array', '!=', null)->where('created_at', '>=', Carbon::now()->subDays(30))->get();
        $translations = Rule34::$translations;
        $removed = Rule34::$removed;
        foreach ($videos as $video) {
            $tags_array = $video->tags_array;
            foreach (array_keys($tags_array) as $tag) {
                if (array_key_exists($tag, $translations)) {
                    $tags_array[$translations[$tag]] = 10;
                    unset($tags_array[$tag]);
                }
                if (in_array($tag, $removed)) {
                    unset($tags_array[$tag]);
                }
            }
            $video->tags_array = $tags_array;
            $video->tags = implode(' ', array_keys($tags_array));

            $title = $video->title;
            $character = Helper::get_string_between($title, '] ', ' [');
            if (array_key_exists($character, $translations)) {
                $title = str_replace($character, $translations[$character], $title);
            }
            $copyright = str_replace(']', '', explode(' [', $title)[1]);
            if (array_key_exists($copyright, $translations)) {
                $title = str_replace($copyright, $translations[$copyright], $title);
            }
            $video->title = $title;
            $video->caption = $title;
            $video->translations = ['JP' => $title];

            $video->save();
        }
    }
